import {Component, OnInit} from '@angular/core';
import {LoadingService} from "../services/loading.service";

@Component({
  selector: 'loading',
  template: '<div *ngIf="loading" style="position: fixed; bottom: 10px; right: 0px; width: 20px;">' +
  '<img src="data:image/gif;base64," /></div>'
})

export class LoadingComponent implements OnInit {
  public loading = false;

  public constructor(private _loading: LoadingService) {}

  public ngOnInit() {
    this._loading._loading.subscribe((status: boolean) => {
      this.loading = status;
    })
  }
}
